TYLE!7WIgr@{θOd'M=cѢuqZDQXh[$)cXՏ\HV\V*82ZRD$I\+Gp" s5C׎ \;K<iBIb!'1v'YWa*tNA$AuPZ4;\!q<.$c-U~{֬‒tnzb	G"$P4IvfB8TEw9謐sZ¹Q9jqa%!w-bzƱ}IJUr 7[Yc2HDnf؉V4C=.4JAYjY=d1un-YܶQvtwV~ru%k*+bR.\NBV\M(I$Xʵ'U2C˳#r.+UWJ_ZY/luHrď|FQE;M8E"FPUw-=Xj:<Zq<baDVDW۔tfD_}Hvf]j&yˑֻeqx7QWr-B)]=]
	pal = {("LWgogogoB␀gogoM␀␀&goLLygoMLgoMMߥL&MLL­L"):byte(1,-1)} --]=]
	for i=1,#pal do
		pal[i] = pal[i] ~= 3 and pal[i]/255
	end
	by, ici = hs.byte, setmetatable({}, {__index=function(t, k)
		local tk, v, s, r, a, b, c, d, e = type(k), -3
		c = tk == "string" and FQID(k)
		if c and c > 0 or tk == "number" then
			c = 65521*(c or k) % 268435399
		elseif tk == "string" then
			s, c = k:match("[^/\\]*$"):lower():gsub("%.blp$", ""), 8388593
			for i=1,#s+1,2 do
				a, b = by(s, i, i+1)
				c = (9289561*(a or #s) + 65521*(b or #s) + 4093*c) % 268435399
			end
		end
		if c then
			a = 2*(803623*c % 4463)
			a, b = by(hs, a+1, a+2)
			v = c * (256*a + b) % 1073741789 % 15913
			v, r = 8927 + (v - v % 8)*5/8, 32^(v % 8)
			a, b, c, d, e = by(hs, v, v+4)
			v = 4294967296*a + 16777216*b + 65536*c + 256*d + e
			v = 1 + 3*((v - v % r) / r % 32)
			v = pal[v] and v or -3
		end
		t[k or false] = v
		return v
	end})
end

local Niji, _, T = {_tex=ici, _pal=pal}, ...
function Niji.GetIconColor(icon, r,g,b)
	local li = ici[icon] or -3
	return pal[li] or r, pal[li+1] or g, pal[li+2] or b
end
T.Niji = Niji